definition.script:
build_ver = '0'
pipeline {
    agent { label 'npm' }
    stages {
        stage('Get code') {
            steps {
                git url: 'https://github.com/ahfarmer/emoji-search.git'
                script {
                    build_ver = readFile('.git/refs/heads/master')
                }
            }
        }
        stage('Print build version') {
            steps {
                echo "${build_ver}"
            }
        }
        stage('Build code') {
            steps {
                sh 'npm install'
            }
        }
        stage('Test code') {
            steps {
                sh 'CI=true npm test'
            }
        }
        stage('Build Container'){
            agent { label 'docker' }
            steps {
                sh 'if [ -d "emoji-search" ];then rm -rf emoji-search; else mkdir emoji-search;fi'
                dir("emoji-search") {
                    checkout([$class: 'GitSCM', branches: [[name: build_ver ]],
     userRemoteConfigs: [[url: 'https://github.com/ahfarmer/emoji-search.git']]])
                sh 'env'
                script {
                    env
                }
                sh 'cp /var/jenkins_home/jobs/build_test_emoji/Dockerfile .'
                sh "docker build -t localhost:5000/node-emoji:$BUILD_NUMBER ."
                sh "docker push localhost:5000/node-emoji:$BUILD_NUMBER"
                }
            }
        }
    }
}
